b = Book.create(:name => 'Adobe Photoshop CS5. Справочник по цифровой фотографии',
		:image_url => '10194367_0_Adobe_Photoshop_CS5_Spravochnik_po_cifrovoy_fotografii_Skott_Kelbi.jpg',
